  title: string;
  price: string;
  image: string;
  color: 'primary' | 'secondary' | 'accent';
}

const ProductCard = ({ title, price, image, color }: ProductCardProps) => {
  const bgColors = {
    primary: 'bg-primary',
    secondary: 'bg-secondary',
    accent: 'bg-accent',
  };

  return (
    <div className="group animate-pop-in">
      {/* Comic frame border */}
